// src/components/FileUpload.js
import React, { useState } from 'react';

const FileUpload = ({ onUpload }) => {
  const [files, setFiles] = useState([]);
  const [description, setDescription] = useState('');
  const [password, setPassword] = useState('');
  const [expiry, setExpiry] = useState('');
  const [error, setError] = useState('');
  const [isUploading, setIsUploading] = useState(false);

  const handleFileChange = (e) => {
    setFiles([...e.target.files]);
  };

  const validateForm = () => {
    if (password && (password.length < 4 || password.length > 12)) {
      setError('Password must be between 4 and 12 characters.');
      return false;
    }
    if (expiry && (isNaN(expiry) || expiry <= 0)) {
      setError('Expiry must be a positive number.');
      return false;
    }
    setError('');
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsUploading(true);
    const uploadedFiles = files.map(file => ({
      file,
      description,
      password,
      expiry,
      uploadDate: new Date().toLocaleDateString()
    }));
    onUpload(uploadedFiles);
    setFiles([]);
    setDescription('');
    setPassword('');
    setExpiry('');
    setIsUploading(false);
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-bold mb-4">File Upload</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleSubmit}>
        <input 
          type="file" 
          multiple 
          onChange={handleFileChange} 
          className="block w-full mb-4"
          required
        />
        <input
          type="text"
          placeholder="File Description (optional)"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="block w-full mb-4 p-2 border rounded"
        />
        <input
          type="password"
          placeholder="Password (4-12 characters)"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="block w-full mb-4 p-2 border rounded"
        />
        <input
          type="number"
          placeholder="Expire after (days)"
          value={expiry}
          onChange={(e) => setExpiry(e.target.value)}
          className="block w-full mb-4 p-2 border rounded"
        />
        <button 
          type="submit" 
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          disabled={isUploading}
        >
          {isUploading ? 'Uploading...' : 'Upload'}
        </button>
      </form>
    </div>
  );
};

export default FileUpload;
