import React from 'react';

const formatFileSize = (size) => {
  if (size < 1024) return `${size} B`;
  if (size < 1024 * 1024) return `${(size / 1024).toFixed(2)} KB`;
  if (size < 1024 * 1024 * 1024) return `${(size / (1024 * 1024)).toFixed(2)} MB`;
  return `${(size / (1024 * 1024 * 1024)).toFixed(2)} GB`;
};

const handleDownload = (file) => {
  const url = URL.createObjectURL(file);
  const link = document.createElement('a');
  link.href = url;
  link.download = file.name;
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

const UploadedFiles = ({ files, onDelete }) => {
  return (
    <div className="max-w-4xl mx-auto p-4 bg-white rounded-lg shadow-md mt-6 overflow-x-auto">
      <h2 className="text-xl font-bold mb-4">Uploaded Files</h2>
      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2 px-4 border-b">File Name</th>
            <th className="py-2 px-4 border-b">File Size</th>
            <th className="py-2 px-4 border-b">Upload Date</th>
            <th className="py-2 px-4 border-b">Description</th>
            <th className="py-2 px-4 border-b">Action</th>
          </tr>
        </thead>
        <tbody>
          {files.map((fileData, index) => (
            <tr key={index} className='p-2'>
              <td className="py-2 px-4 border-b">{fileData.file.name}</td>
              <td className="py-2 px-4 border-b">{formatFileSize(fileData.file.size)}</td>
              <td className="py-2 px-4 border-b">{fileData.uploadDate}</td>
              <td className="py-2 px-4 border-b">{fileData.description || 'N/A'}</td>
              <td className="py-2 px-4 border-b flex space-x-2">
                <button 
                  className="bg-green-500 text-white p-2 rounded hover:bg-green-600"
                  onClick={() => handleDownload(fileData.file)}
                >
                  Download
                </button>
                <button 
                  className="bg-red-500 text-white p-2 rounded hover:bg-red-600"
                  onClick={() => onDelete(index)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UploadedFiles;
