// src/components/UploadedFiles.js
import React from 'react';

const UploadedFiles = ({ files }) => {
  return (
    <div className="max-w-4xl mx-auto p-4 bg-white rounded-lg shadow-md mt-6">
      <h2 className="text-xl font-bold mb-4">Uploaded Files</h2>
      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2 px-4 border-b">File Name</th>
            <th className="py-2 px-4 border-b">File Size</th>
            <th className="py-2 px-4 border-b">Upload Date</th>
            <th className="py-2 px-4 border-b">Description</th>
            <th className="py-2 px-4 border-b">Action</th>
          </tr>
        </thead>
        <tbody>
          {files.map((fileData, index) => (
            <tr key={index}>
              <td className="py-2 px-4 border-b">{fileData.file.name}</td>
              <td className="py-2 px-4 border-b">{(fileData.file.size / 1024).toFixed(2)} KB</td>
              <td className="py-2 px-4 border-b">{fileData.uploadDate}</td>
              <td className="py-2 px-4 border-b">{fileData.description || 'N/A'}</td>
              <td className="py-2 px-4 border-b">
                <button className="bg-green-500 text-white p-2 rounded hover:bg-green-600">
                  Download
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UploadedFiles;
