import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import UploadedFiles from './components/UploadedFiles';
import './index.css';

const App = () => {
  const [uploadedFiles, setUploadedFiles] = useState([]);

  const handleFilesUpload = (newFiles) => {
    setUploadedFiles([...uploadedFiles, ...newFiles]);
  };

  const handleFileDelete = (index, password) => {
    const file = uploadedFiles[index];
    if (file.password && file.password !== password) {
      alert('Incorrect password. File cannot be deleted.');
      return;
    }
    setUploadedFiles(uploadedFiles.filter((_, i) => i !== index));
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <FileUpload onUpload={handleFilesUpload} />
      <UploadedFiles files={uploadedFiles} onDelete={handleFileDelete} />
    </div>
  );
};

export default App;
